
public class Page200 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i=0; i<3; i++) { //3번 반복하고 싶으면, 컴퓨터가 0부터 세는걸 적용해서 
			System.out.println("반복 i값"+i);
			System.out.println("##또 안녕하세요?## \n");
		}
		
		System.out.println("\n \n");
		
		for(i=0; i<3; i++)
			System.out.println("반복 i값"+i);
			System.out.println("##또 안녕하세요?## \n");
		}
}
